{-# LANGUAGE OverloadedStrings #-}
{-# LANGUAGE TypeApplications  #-}
{-| Constants for muesli contract on mainnet
-}
module Convex.Muesli.Orderbook.Constants(
  muesliScriptJSON,
  muesliTextEnvelope,
  muesliScript,
  muesliScript',
  muesliHash,
  muesliAddress,
  fullMatchRedeemer,
  muesliVersion,
  MuesliVersion(..)
) where

import           Cardano.Api                      (NetworkId, PlutusScript,
                                                   PlutusScriptV1, Script,
                                                   ScriptHash, TextEnvelope)
import qualified Cardano.Api                      as C
import qualified Cardano.Api.Shelley              as C
import           Convex.Muesli.Orderbook.Contract (OrderAction (..))
import           Data.Aeson                       (Result (..), Value, fromJSON,
                                                   object, (.=))
import           Data.Proxy                       (Proxy (..))
import qualified Plutus.V1.Ledger.Api             as Plutus


muesliScriptJSON :: Value
muesliScriptJSON =
  object
    [ "type"        .= s "PlutusScriptV1"
    , "description" .= s ""
    , "cborHex"     .= s ""
    ]

muesliTextEnvelope :: TextEnvelope
muesliTextEnvelope = case fromJSON muesliScriptJSON of
  Success e -> e
  Error err   -> error ("failed to decode muesliScriptJSON text envelope: " <> err)

muesliScript :: Script PlutusScriptV1
muesliScript = case C.deserialiseFromTextEnvelope (C.proxyToAsType $ Proxy @(Script PlutusScriptV1)) muesliTextEnvelope of
  Left err -> error ("muesliScript: failed to deserialise muesli script: " <> show err)
  Right x  -> x

muesliScript' :: PlutusScript PlutusScriptV1
muesliScript' = case C.deserialiseFromTextEnvelope (C.proxyToAsType $ Proxy @(PlutusScript PlutusScriptV1)) muesliTextEnvelope of
  Left err -> error ("muesliScript': failed to deserialise muesli script: " <> show err)
  Right x  -> x

muesliHash :: ScriptHash
muesliHash = C.hashScript muesliScript

muesliAddress :: NetworkId -> C.AddressInEra C.BabbageEra
muesliAddress network = C.makeShelleyAddressInEra network (C.PaymentCredentialByScript muesliHash) C.NoStakeAddress

s :: String -> String
s = id

fullMatchRedeemer :: C.ScriptRedeemer
fullMatchRedeemer = C.fromPlutusData (Plutus.toData FullMatch)

data MuesliVersion = V1
  deriving Show

muesliVersion :: a -> ScriptHash -> Maybe MuesliVersion
muesliVersion _ sc
  | sc == muesliHash = Just V1
  | otherwise        = Nothing
